return [{
  sessionId: $('Execute Workflow Trigger').first().json.query.sessionId,
    task_id: $('Execute Workflow Trigger').first().json.query.task_id,

  formattedString: `<message>
${$('Execute Workflow Trigger').first().json.query.message}
</message>

<Questions>
${JSON.stringify($input.all(), null, 2)}
</Questions>

<info_jobs>
${JSON.stringify($('Puesto_Al_Que_Aplica').first().json, null, 2)}
</info_jobs>

<info_apply>
${JSON.stringify($('Quien_Es').first().json, null, 2)}
</info_apply>`
}];

// Mapeo de c√≥digos de pa√≠s a zonas horarias y nombres
const countryTimezones = {
  // Am√©rica Latina
  "51": { timezone: "America/Lima", country: "Per√∫", hasDST: false },
  "52": { timezone: "America/Mexico_City", country: "M√©xico", hasDST: true },
  "54": { timezone: "America/Argentina/Buenos_Aires", country: "Argentina", hasDST: false },
  "55": { timezone: "America/Sao_Paulo", country: "Brasil", hasDST: false },
  "56": { timezone: "America/Santiago", country: "Chile", hasDST: true },
  "57": { timezone: "America/Bogota", country: "Colombia", hasDST: false },
  "58": { timezone: "America/Caracas", country: "Venezuela", hasDST: false },
  "593": { timezone: "America/Guayaquil", country: "Ecuador", hasDST: false },
  "59": { timezone: "America/Guayaquil", country: "Ecuador", hasDST: false },
  "50": { timezone: "America/Panama", country: "Panam√°", hasDST: false },
  "1": { timezone: "America/New_York", country: "Estados Unidos/Canad√°", hasDST: true },
  "34": { timezone: "Europe/Madrid", country: "Espa√±a", hasDST: true },
  "44": { timezone: "Europe/London", country: "Reino Unido", hasDST: true },
  "49": { timezone: "Europe/Berlin", country: "Alemania", hasDST: true },
  "33": { timezone: "Europe/Paris", country: "Francia", hasDST: true }
};

// Extraer sessionId y procesar
const sessionId = query.sessionId;

// Extraer c√≥digo de pa√≠s (primeros d√≠gitos)
let countryCode = sessionId.match(/^(\d+)/);
countryCode = countryCode ? countryCode[0] : null;

// Buscar pa√≠s en el mapa
let countryInfo = null;

// Intentar con c√≥digo completo primero
if (countryCode && countryTimezones[countryCode]) {
  countryInfo = countryTimezones[countryCode];
} 
// Luego con 2 primeros d√≠gitos
else if (countryCode && countryCode.length >= 2 && countryTimezones[countryCode.substring(0, 2)]) {
  countryInfo = countryTimezones[countryCode.substring(0, 2)];
} 
// Finalmente con primer d√≠gito
else if (countryCode && countryCode.length >= 1 && countryTimezones[countryCode.substring(0, 1)]) {
  countryInfo = countryTimezones[countryCode.substring(0, 1)];
} 
// Predeterminado si no se encuentra
else {
  countryInfo = { 
    country: "Internacional", 
    timezone: "UTC", 
    hasDST: false 
  };
}

// Obtener hora local
const now = new Date();
let localTime, hour, greeting;

try {
  // Intenta obtener hora local en formato ES
  localTime = now.toLocaleString('es-ES', {
    timeZone: countryInfo.timezone,
    hour12: false
  });

  // Extraer hora para saludo
  hour = new Date(localTime).getHours();

  // Determinar saludo seg√∫n hora
  if (hour >= 5 && hour < 12) {
    greeting = "buenos d√≠as";
  } else if (hour >= 12 && hour < 19) {
    greeting = "buenas tardes";
  } else {
    greeting = "buenas noches";
  }
} catch (e) {
  // Fallback simple
  localTime = now.toISOString();
  greeting = "saludos";
  hour = now.getUTCHours();
}

// Determinar estaci√≥n para pa√≠ses del sur
const currentMonth = now.getMonth() + 1;
const isWinterInSouthernHemisphere = currentMonth >= 4 && currentMonth <= 9;
const isSouthernCountry = ["56", "54", "55"].includes(countryCode);
const season = isSouthernCountry
  ? (isWinterInSouthernHemisphere ? "invierno" : "verano")
  : (isWinterInSouthernHemisphere ? "verano" : "invierno");

// Crear objeto con toda la informaci√≥n
const result = {
  country: countryInfo.country,
  countryCode: countryCode || "desconocido",
  timezone: countryInfo.timezone,
  localTime: localTime,
  greeting: greeting,
  hour: hour,
  season: season
};

// Retornar como string JSON
return JSON.stringify(result);

