/**
 * Herramienta para detectar zona horaria basada en el c√≥digo de pa√≠s
 * Extrae el c√≥digo de pa√≠s de los primeros caracteres del sessionId
 * y devuelve informaci√≥n horaria relevante.
 */

// Mapeo de c√≥digos de pa√≠s a zonas horarias y nombres
const countryTimezones = {
  // Am√©rica Latina
  "51": { timezone: "America/Lima", country: "Per√∫", hasDST: false },
  "52": { timezone: "America/Mexico_City", country: "M√©xico", hasDST: true },
  "54": { timezone: "America/Argentina/Buenos_Aires", country: "Argentina", hasDST: false },
  "55": { timezone: "America/Sao_Paulo", country: "Brasil", hasDST: false },
  "56": { timezone: "America/Santiago", country: "Chile", hasDST: true },
  "57": { timezone: "America/Bogota", country: "Colombia", hasDST: false },
  "58": { timezone: "America/Caracas", country: "Venezuela", hasDST: false },
  "593": { timezone: "America/Guayaquil", country: "Ecuador", hasDST: false },
  "59": { timezone: "America/Guayaquil", country: "Ecuador", hasDST: false }, // Mantener compatibilidad
  "50": { timezone: "America/Panama", country: "Panam√°", hasDST: false },

  // Norteam√©rica
  "1": { timezone: "America/New_York", country: "Estados Unidos/Canad√°", hasDST: true },

  // Europa
  "34": { timezone: "Europe/Madrid", country: "Espa√±a", hasDST: true },
  "44": { timezone: "Europe/London", country: "Reino Unido", hasDST: true },
  "49": { timezone: "Europe/Berlin", country: "Alemania", hasDST: true },
  "33": { timezone: "Europe/Paris", country: "Francia", hasDST: true },

  // Predeterminado si no se encuentra
  "default": { timezone: "UTC", country: "Internacional", hasDST: false }
};

/**
 * Procesa el input del agente y extrae informaci√≥n de zona horaria
 * @param {Object} input - Objeto de entrada con informaci√≥n de la consulta
 * @returns {Object} - Informaci√≥n sobre zona horaria y hora local
 */
function processTimezoneInfo(input) {
  try {
    // Extraer el sessionId del input
    const sessionId = input.query.sessionId;

    // Extraer c√≥digo de pa√≠s (primeros 2 d√≠gitos antes del primer no-d√≠gito)
    let countryCode = sessionId.match(/^(\d+)/);
    countryCode = countryCode ? countryCode[0] : null;

    // Intentar con los primeros 2 d√≠gitos, luego con el primero si es necesario
    let timezoneInfo = null;

    if (countryCode && countryCode.length >= 2) {
      // Probar con los primeros 2 d√≠gitos
      timezoneInfo = countryTimezones[countryCode.substring(0, 2)];

      // Si no se encuentra, probar con el primer d√≠gito (para c√≥digos como '1')
      if (!timezoneInfo && countryCode.length >= 1) {
        timezoneInfo = countryTimezones[countryCode.substring(0, 1)];
      }
    }

    // Usar predeterminado si no se encontr√≥ coincidencia
    if (!timezoneInfo) {
      timezoneInfo = countryTimezones.default;
      countryCode = "desconocido";
    }

    // Obtener la hora actual en la zona horaria correcta
    const now = new Date();
    const options = {
      timeZone: timezoneInfo.timezone,
      hour12: false,
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit',
      year: 'numeric',
      month: '2-digit',
      day: '2-digit'
    };

    // Formatear la fecha seg√∫n la zona horaria
    let localTime;
    try {
      localTime = now.toLocaleString('es-ES', options);
    } catch (e) {
      // Fallback si hay problemas con toLocaleString
      localTime = now.toISOString();
    }

    // Calcular el desplazamiento (offset) actual basado en la hora local
    // Esto manejar√° autom√°ticamente el horario de verano/invierno
    const localDate = new Date(localTime);
    const utcDate = new Date(now.toISOString());

    // Calcular diferencia en minutos entre hora local y UTC
    const offsetMinutes = (localDate.getTime() - utcDate.getTime()) / 60000;

    // Formatear offset como +/-HH:MM
    const offsetHours = Math.floor(Math.abs(offsetMinutes) / 60);
    const offsetMins = Math.abs(offsetMinutes) % 60;
    const offsetSign = offsetMinutes >= 0 ? '+' : '-';
    const offset = `${offsetSign}${String(offsetHours).padStart(2, '0')}:${String(offsetMins).padStart(2, '0')}`;

    // Determinar si est√° en horario de verano
    const isDST = timezoneInfo.hasDST && 
                  (function() {
                    // M√©todo simple para detectar DST: comparar offset en enero vs ahora
                    const januaryDate = new Date(now.getFullYear(), 0, 1);
                    const januaryOptions = {...options};
                    const januaryString = januaryDate.toLocaleString('es-ES', januaryOptions);
                    const januaryLocal = new Date(januaryString);
                    const januaryUTC = new Date(januaryDate.toISOString());
                    const januaryOffset = (januaryLocal.getTime() - januaryUTC.getTime()) / 60000;

                    return Math.abs(offsetMinutes) !== Math.abs(januaryOffset);
                  })();

    // Determinar la estaci√≥n actual (para Chile y otros pa√≠ses con DST)
    const currentMonth = now.getMonth() + 1; // 1-12
    const isWinterInSouthernHemisphere = currentMonth >= 4 && currentMonth <= 9;
    const season = (countryCode === "56" || countryCode === "54" || countryCode === "55") 
                    ? (isWinterInSouthernHemisphere ? "invierno" : "verano")
                    : (isWinterInSouthernHemisphere ? "verano" : "invierno");

    // Determinar saludo seg√∫n la hora local
    // Extraer hora de la fecha local
    const hourMatch = localTime.match(/(\d{2}):/);
    const hour = hourMatch ? parseInt(hourMatch[1]) : 12;

    let greeting = "";
    if (hour >= 5 && hour < 12) {
      greeting = "buenos d√≠as";
    } else if (hour >= 12 && hour < 19) {
      greeting = "buenas tardes";
    } else {
      greeting = "buenas noches";
    }

    // Retornar objeto con informaci√≥n completa
    return {
      countryCode: countryCode,
      country: timezoneInfo.country,
      timezone: timezoneInfo.timezone,
      offset: offset,
      isDST: isDST,
      season: season,
      localTime: localTime,
      hour: hour,
      greeting: greeting,
      timestamp: now.toISOString(),
    };
  } catch (error) {
    // En caso de error, devolver informaci√≥n b√°sica
    return {
      error: "Error procesando informaci√≥n de zona horaria",
      errorDetail: error.message,
      timestamp: new Date().toISOString(),
      timezone: "UTC",
      greeting: "saludos"
    };
  }
}

// Funci√≥n principal que maneja la entrada a la herramienta
function getCountryTimezone(input) {
  // Verificar que el input tenga la estructura esperada
  if (!input || !input.query || !input.query.sessionId) {
    return {
      error: "Formato de entrada inv√°lido",
      timestamp: new Date().toISOString()
    };
  }

  // Procesar y retornar la informaci√≥n de zona horaria
  return processTimezoneInfo(input);
}

// C√≥digo para procesar la entrada
module.exports = function(input) {
  return getCountryTimezone(input);
};

