// Funci√≥n para determinar el pa√≠s basado en el c√≥digo
function determinarPais(sessionId) {
  // Extraer c√≥digo de pa√≠s (primeros d√≠gitos antes de cualquier no-d√≠gito)
  const match = sessionId.match(/^(\d+)/);
  if (!match) return "desconocido";

  const codigo = match[0];

  // Mapeo de c√≥digos a pa√≠ses
  const mapaPaises = {
    "51": "Per√∫",
    "52": "M√©xico",
    "54": "Argentina",
    "55": "Brasil",
    "56": "Chile",
    "57": "Colombia",
    "58": "Venezuela",
    "593": "Ecuador",
    "50": "Panam√°",
    "1": "Estados Unidos/Canad√°",
    "34": "Espa√±a",
    "44": "Reino Unido",
    "49": "Alemania",
    "33": "Francia"
  };

  // Verificar c√≥digo de pa√≠s completo primero (para casos como 593)
  if (mapaPaises[codigo]) return mapaPaises[codigo];

  // Verificar los primeros 2 d√≠gitos
  if (codigo.length >= 2 && mapaPaises[codigo.substring(0, 2)]) 
    return mapaPaises[codigo.substring(0, 2)];

  // Verificar el primer d√≠gito (para c√≥digos como 1)
  if (codigo.length >= 1 && mapaPaises[codigo.substring(0, 1)]) 
    return mapaPaises[codigo.substring(0, 1)];

  return "desconocido";
}


return [{
  sessionId: $('Execute Workflow Trigger').first().json.query.sessionId,
   task_id: $('Execute Workflow Trigger').first().json.query.task_id,
  country: determinarPais($('Execute Workflow Trigger').first().json.query.sessionId),
  formattedString: `<message>
${$('Execute Workflow Trigger').first().json.query.message}
</message>
<country>${determinarPais($('Execute Workflow Trigger').first().json.query.sessionId)}</country>`
}];
